    private void btnencriptarfirmarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnencriptarfirmarActionPerformed

        String mensaje = txtmensaje.getText(); // el valor del cuadro de texto lo asignamos a variable
        if(mensaje.equals("")) { // si la variable esta vacia
            // mandamos mensaje de error
            JOptionPane.showMessageDialog(null, "DEBE INGRESAR UN MENSAJE", "CAMPO DE MENSAJE VACIO", JOptionPane.ERROR_MESSAGE);
            txtmensaje.setText(""); // limpiamos el cuadro de texto
        }else{
            try {
                txtmensaje.enable(false);
                // INSTANCIAMOS LA CLASE QUE GENERA LAS LLAVES, ENCRIPTA Y DESENCRIPTA Y LA FIRMA DIGITAL
                LLAVES llaves = new LLAVES();
                // GENERAMOS LAS LLAVES: PUBLICA Y PROVADA
                KeyPair parllaves = llaves.generarLlaves();
                // ENCRIPTAMOS EL MENSAJE Y LO MOSTRAMOS EN CAJA DE TEXTO
                String textocifrado = llaves.encriptar(mensaje, parllaves.getPublic());
                // DESENCRIPTAMOS EL MENSAJE Y LO MOSTRAMOS
                String textodescifrado = llaves.desencriptar(textocifrado, parllaves.getPrivate());
                // FIRMA DIGITAL DEL MENSAJE
                String firma = llaves.firma(mensaje, parllaves.getPrivate());
                // VALIDAMOS LA FIRMA
                boolean esvalida = llaves.verificar(mensaje, firma, parllaves.getPublic());
                // DEPENDE DEL RESULTADO (TRUE FALSE) MOSTRAREMOS LOS RESULTADOS EN LOS CAMPOS
                // O MANDAMOS MENSAJE DE ERROR
                String respuesta = Boolean.toString(esvalida); // el resultado lo asignamos a variable
                if(respuesta.equals("true")){ // si es true (firma valida
                    txtareafirma.setLineWrap(true); // para que al llegar al final del control, se siga en la siguiente linea
                    txtencriptado.setText(textocifrado); // mostramos el texto encriptado
                    txtdesencriptado.setText(textodescifrado); // mostramos el texto desencriptado con el metodo
                    txtareafirma.setText(firma); // mostramos la firma
                    // y en un mensaje mostramos la validez de la firma
                    JOptionPane.showMessageDialog(null, "MENSAJE CIFRADO Y FIRMADO", "RESPUESTA", JOptionPane.INFORMATION_MESSAGE);
                }
              } catch (Exception ex) {
                // si la generacion de las llaves, encriptado y firma falla, lanzo excepcion y muestro
                // mensaje de error
                txtmensaje.setText("NO SE PUDO ENCRIPTAR / FIRMAR EL MENSAJE /n" + ex);
                // limpio nuevamente los elementos de la ventana para nueva captura
                txtencriptado.setText("");
                txtdesencriptado.setText("");
                txtareafirma.setText("");  
            }   
        }
    }//GEN-LAST:event_btnencriptarfirmarActionPerformed

