    private void btnnuevoActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnnuevoActionPerformed
        txtmensaje.enable(true); // habilitamos el cuadro de texto del mensaje
        // limpiamos los elementos para que sean llenados nuevamente
        txtmensaje.setText("");
        txtencriptado.setText("");
        txtdesencriptado.setText("");
        txtareafirma.setText("");
    }//GEN-LAST:event_btnnuevoActionPerformed

